catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }

    //Type appropriate comment here, and begin script below
    function onChange(control, oldValue, newValue, isLoading) {
        if (isLoading || !newValue) return;

        var ga = new GlideAjax('BillingUtilsPractice');
        ga.addParam('sysparm_name', 'recordsByPatient');
        ga.addParam('sysparm_patient_id', newValue);

        ga.getXMLAnswer(function(response) {
            var records = JSON.parse(response || '[]');

            if (!records.length) {
                g_form.addErrorMessage("No billing records found.");
                return;
            }

            var msg = "Billing Records:";
            for (var i = 0; i < records.length; i++) {
                msg += records[i].total_amount + " " + records[i].status ;
            }
            g_form.addInfoMessage(msg);
        });
    }

}
