sys_script_include.script:
var ScriptIncludeQ1 = Class.create();
ScriptIncludeQ1.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	byPatient:function(){
		var id=this.parameter('sysparm_id');
		var result=[];
		var gr=new GlideRecord('u_patient_record');
		if(gr.get(id)){
			result.push({
				sys_id:gr.getUniqueValue()+'',
				status:gr.u_status+'',
				name:gr.u_first_name+''+gr.u_last_name
			});
		}
		return result;
	},

	isVIP:function(){
		var id=this.parameter('sysparm_id');
		var  gr=new GlideRecord('u_patient_record');
		if(gr.get(id)){
			return gr.u_vip=='true' || gr.u_vip==true;
		}
		return false;
	},

	age:function(){
		var id=this.parameter('sysparm_id');
		var gr=new GlideRecord('u_patient_record');
		if(gr.get(id)){
			var today=new GlideDateTime();
			var dob=new GlideDateTime(gr.u_date_of_birth);
			var diff=today.getNumericValue()-dob.getNumericValue();
			var year=1000*60*60*24*365.25;
			var result=Math.floor(diff/year);
		}
		return result;
	},

	isConfirmed:function(){
		var id=this.parameter('sysparm_id');
		var gr=new GlideRecord('u_appointment');
		if(gr.get(id)){
			return (gr.u_status=='Confirmed').toString();
		}return false;
	},

	byDate:function(){
		var res=[];
		var date=this.parameter('sysparm_date');
		var gr=new GlideRecord('u_appointment');
		gr.addQuery('u_appointment_date','STARTSWITH',date);
		gr.query();
		while(gr.next()){
			res.push({
				sys_id: gr.getUniqueValue(),
                patient: gr.u_patient_id.getDisplayValue(),
                status: gr.u_status + ''
			});
		}
		return res;
	},

	byDepartment:function(){
		var res=[];
		var deprt=this.parameter('sysparm_department');
		var gr=new GlideRecord('u_appointment');
		gr.addQuery('u_department',deprt);
		gr.query();
		while(gr.get(id)){
			res.push({
				sys_id:gr.getUniqueValue()+'',
				status:gr.u_status
			});
		}
		return res;
	},

    type: 'ScriptIncludeQ1'
});
