sys_script_include.script:
var  AppointmentUtilsPractice = Class.create();
 AppointmentUtilsPractice.prototype = Object.extendObject(AbstractAjaxProcessor,{
    initialize: function() {
    },
	allAppointments:function(){
		var id=this.parameter('sysparm_patient_id');
		var result=[];
		var gr=new GlideRecord('u_appointment');
		gr.addQuery('u_patient_id',id);
		gr.query();
		if(gr.next()){
			result.push({
				sys_id: gr.getUniqueValue(),
                date: gr.u_appointment_date.getDisplayValue(),
                status: gr.u_status + ''
			});
		}
		return JSON.stringify(result);
	},

	isConfirmed:function(){
		var id=this.parameter('sysparm_id');
		var gr=new GlideRecord('u_appointment');
		if(gr.get(id)){
			return (gr.u_status=='Confirmed').toString();
		}
		return false;
	},

	detailsByID:function(){
		var id =this.parameter('sysparm_id');
		var result=[];
		var gr=new GlideRecord('u_appointment');
		if(gr.get(id)){
			result.push({
				sys_id:gr.getUniqueValue(),
				patient:gr.u_patient_id.getDisplayValue()+' ',
				status:gr.u_status+' ',
				department:gr.u_department+' ',
			});
		}
		return result;
	},

	detailsDate:function(){
		var date=this.parameter('sysparm_date');
		var result=[];
		var gr =new GlideRecord('u_appointment');
		gr.addQuery('u_appointment_date','STARTSWITH',date);
		gr.query();
		while (gr.next()){
			result.push({
				sys_id: gr.getUniqueValue(),
                patient: gr.u_patient_id.getDisplayValue(),
                status: gr.u_status + ''
			});
		}
		return JSON.stringify(result);
	},

	byDepartent:function(){
		var depart=this.parameter('sysparm_department');
		var result=[];
		var gr = new GlideRecord("u_appointment");
		gr.addQuery('u_department',depart);
		gr.query();
		while(gr.next()){
			result.push({
				sys_id: gr.getUniqueValue(),
                date: gr.u_appointment_date.getDisplayValue(),
                patient: gr.u_patient_id.getDisplayValue()
			});
		}
		return JSON.stringify(result);
	},
    type: ' AppointmentUtilsPractice'
});
